import {Box,Text,Image} from '@chakra-ui/react';
import {AppContext} from '../context/AppContextProvider';
import React from 'react';
import {Link} from 'react-router-dom';
import {useState} from 'react';
import './navbar.css';

function LoggedIn(){
    const value = React.useContext(AppContext);
    const [isHovering, setIsHovering] = useState(false);
    const [showUserName,setShowUserName] = useState(false);

    const handleMouseOver = () => {
        setIsHovering(true);
    };

    const handleMouseOut = () => {
        setIsHovering(false);
    };

    const handleOut=()=>{
        setShowUserName(true);
    }

    const handleIn=()=>{
        setShowUserName(false);
    }

    return(
        <Box display='flex' p='5' flexDirection="row" gap='5' paddingRight='50px'>
            <Box width={{lg:"4rem"}} onMouseOver={handleMouseOver} onMouseOut={handleMouseOut}>
                <Image  src='https://www.kindmeal.my/images/icon_notice.png' alt=''/>
                <Box position='relative' left='5' bottom='5' color='white' cursor='pointer' >0</Box>
                {isHovering && <div className='notification'>
                    <center>
                        <Text>You cuurently have no notifications</Text>
                    </center>
                </div>}
            </Box>
            <Box onMouseOver={handleIn} onMouseOut={handleOut}>
                <Box display='flex' flexDirection='row' gap='5'>
                    <Box display='flex' flexDirection='row' color='#666666'>
                        <Text>Rinki </Text>&nbsp;&nbsp;<Text fontSize='0.8rem' pt='1'>â–¼</Text>
                    </Box>
                    <Box>
                        <Link to='/'>
                            <Image src='https://www.kindmeal.my/images/no_photo_header.png' alt='avatar'/>
                        </Link>
                    </Box>
                </Box>
                <div>
                    <Box>
                        My Coupons
                    </Box>
                    <Box>
                    My Recipes
                    </Box>
                    <Box>
                    My KindMeal Profile
                    </Box>
                    <Box>
                        <center>Update Profile</center>
                    </Box>
                    <Box>
                        <center>Account Setting</center>
                    </Box>
                    <Box>
                        <center>Logout</center>
                    </Box>
                </div>
            </Box>
        </Box>
    )
}

