//import {Box, Stack, Image,Text,Spacer,Flex} from '@chakra-ui/react';
import {useEffect} from 'react';
import './Slider.css';
const data = [
    {
        img1:"https://www.kindmeal.my/photos/deal/6/683-4718-m.jpg",
        brand1:'https://www.kindmeal.my/photos/shop/5/567-4252-m.jpg',
        title:"Creative Home Style Dinning,15%off -Red House 2"
    },
    {
        img2:"https://www.kindmeal.my/photos/deal/6/683-4718-m.jpg",
        brand2:'https://www.kindmeal.my/photos/shop/4/490-3254-m.jpg',
        title:"Memoriable Vegetairian Delights,20%off -Elyn Vegetarian"
    },
    {
        img3:"https://www.kindmeal.my/photos/deal/6/633-3874-m.jpg",
        brand3:'https://www.kindmeal.my/photos/shop/5/512-3751-m.jpg',
        title:"Level Feals,20%off -Love Veggie"
    },
    {
        img4:"https://www.kindmeal.my/photos/deal/6/618-3514-m.jpg",
        brand4:'https://www.kindmeal.my/photos/shop/4/494-3446-m.jpg',
        title:"Trendy Fuision Delights"
    },
    {
        img5:"https://www.kindmeal.my/photos/deal/6/628-4921-m.jpg",
        brand5:'https://www.kindmeal.my/photos/shop/5/505-4609-m.               ',
        title:'Japanese Fusion Delights'
    },
    {
        img6:"https://www.kindmeal.my/photos/deal/4/498-2202-m.jpg",
        brand6:'https://www.kindmeal.my/photos/shop/2/212-2494-m.jpg',
        title:'Imperial Rest,10% off -Implerial firstbColl'
    },
    {
        img7:"https://www.kindmeal.my/photos/deal/6/617-3503-m.jpg",
        brand7:'https://www.kindmeal.my/photos/shop/4/493-3436-m.jpg',
        title:"Earth Friendly Meals,15off -LOovem "
    }
]



function Slider(){

    let slideIndex = 1;

    setInterval(()=>{
        if(slideIndex===7){
            slideIndex=1
        }
        slideIndex++;
      showSlides(slideIndex);
    },9000)

    const handleCurrentSlide = (n)=>{
        showSlides(slideIndex = n)
    }

    function showSlides(n) {
        let i;
        let slides = document.getElementsByClassName("mySlides");
        let dots = document.getElementsByClassName("demo");
        let captionText = document.getElementById("caption");
        if (n > slides.length) {slideIndex = 1}
        if (n < 1) {slideIndex = slides.length}
        for (i = 0; i < slides.length; i++) {
            slides[i].style.display = "none";
        }
        for (i = 0; i < dots.length; i++) {
            dots[i].className = dots[i].className.replace(" active", "");
        }
        slides[slideIndex-1].style.display = "block";
        dots[slideIndex-1].className += " active";
        captionText.innerHTML = dots[slideIndex-1].alt;
    } 

    useEffect(()=>{
        showSlides(1);
    },[])

    return(
        <div className="container" style={{height:"493px"}}>

            {/* <!-- Full-width images with number text --> */}
            <div className="mySlides">
                <img src="https://www.kindmeal.my/photos/deal/6/683-4718-m.jpg" style={{width:"70%",height:"350px"}} alt="slide-1"/>
            </div>

            <div className="mySlides">
                <img src="https://www.kindmeal.my/photos/deal/5/590-3137-m.jpg" style={{width:"70%",height:"350px"}} alt="slide-1"/>
            </div>

            <div className="mySlides">
                <img src="https://www.kindmeal.my/photos/deal/6/633-3874-m.jpg" style={{width:"100%",height:"350px"}} alt="slide-1"/>
            </div>

            <div className="mySlides">
                <img src="https://www.kindmeal.my/photos/deal/6/618-3514-m.jpg" style={{width:"100%",height:"350px"}} alt="slide-1"/>
            </div>

            <div className="mySlides">
                <img src="https://www.kindmeal.my/photos/deal/6/628-4921-m.jpg" style={{width:"100%",height:"350px"}} alt="slide-1"/>
            </div>

            <div className="mySlides">
                <img src="https://www.kindmeal.my/photos/deal/4/498-2202-m.jpg" style={{width:"100%",height:"350px"}} alt="slide-1"/>
            </div>

            {/* <!-- Image text --> */}
            <div className="caption-container">
                <p id="caption"></p>
            </div>

    {/* <!-- Thumbnail images --> */}
        <div className="row" style={{padding:"10px"}}>
            <div className="column">
            <img className="demo cursor" src="https://www.kindmeal.my/photos/deal/6/683-4718-m.jpg" style={{width:"90%",height:"100px"}} onClick={()=>handleCurrentSlide(1)} alt="Creative Home - Style Dinning,15%off -Red House 2"/>
            </div>
            <div className="column">
            <img className="demo cursor" src="https://www.kindmeal.my/photos/deal/5/590-3137-m.jpg" style={{width:"90%",height:"100px"}} onClick={()=>handleCurrentSlide(2)} alt="Memorial Vegetairian Delights, 20%off -Elym Vegetairian"/>
            </div>
            <div className="column">
            <img className="demo cursor" src="https://www.kindmeal.my/photos/deal/6/633-3874-m.jpg" style={{width:"90%",height:"100px"}} onClick={()=>handleCurrentSlide(3)} alt="Lovely Fusion "/>
            </div>
            <div className="column">
            <img className="demo cursor" src="https://www.kindmeal.my/photos/deal/6/618-3514-m.jpg" style={{width:"100%"}} onClick={()=>handleCurrentSlide(4)} alt="Northern Lights"/>
            </div>
            <div className="column">
            <img className="demo cursor" src="https://www.kindmeal.my/photos/deal/6/628-4921-m.jpg" style={{width:"100%"}} onClick={()=>handleCurrentSlide(5)} alt="Nature and sunrise"/>
            </div>
            <div className="column">
            <img className="demo cursor" src="https://www.kindmeal.my/photos/deal/4/498-2202-m.jpg" style={{width:"100%"}} onClick={()=>handleCurrentSlide(6)} alt="Snowy Mountains"/>
            </div>
        </div>
    </div>
    )
}

export default Slider;
